import { useState } from "react";
import { useEffect } from "react";
import axios from "axios";

export function Messages({currentUser, selectedRoom}) {
    const [currentMessage, setCurrentMessage] = useState("");
    const [messages, setMessages] = useState([]);

    function handleMessageChange(e) {
        setCurrentMessage(e.target.value);
    }

    function handleMessageSend(e) {
        e.preventDefault();
        setCurrentMessage("");
    }
    async function loadMessages() {
        const result = await axios.post("/getmessages", {selectedRoom: selectedRoom});
        setMessages(result);
        console.log(result);
    }

    useEffect(function() {
        loadMessages();
    }, []);

    return (
        <>
            <div className="messagesContainer1">
                <div className="messagesContainer2">
                    {messages.map(function(message) {
                        return (
                            <>
                                {message[4] && <span>{message[3]}</span>}
                                {message[2] && <span>{message[1]}</span>}
                                <div className={message[1] === currentUser? "userMessage" : "otherMessage"}>
                                    {message[0]} 
                                </div>
                            </>
                        )
                    })}
                </div>
            </div>
            <div className="messageContainer">
                <form onSubmit={handleMessageSend}>
                    <input 
                        value={currentMessage}
                        onChange={handleMessageChange}
                        type="text"
                        placeholder={`message ${selectedRoom}`}
                        className="messageInput"
                    />
                    <button className="sendButton">Send</button>
                </form>
            </div>
        </>
    )
}
